let randomNumber=Math.floor(100*Math.random())+1,form=document.getElementById("guess-form"),resultDiv=document.getElementById("result");form.addEventListener("submit",e=>{e.preventDefault();e=parseInt(document.getElementById("guess").value,10);e===randomNumber?(resultDiv.textContent="Parabéns! Você acertou o número!",resultDiv.style.color="green"):(e>randomNumber?resultDiv.textContent="O número é menor! Tente novamente.":resultDiv.textContent="O número é maior! Tente novamente.",resultDiv.style.color="red")});